clc;clear;close all

rng(2)

sigSize = 200;
x = (1:sigSize).';

signal = floor(20*sin(x/sigSize *8*pi) + randi(10, sigSize, 1)) + 100*(rand(sigSize, 1) < .02);

windowSize = 5;
medFilt = dsp.MedianFilter('WindowLength', windowSize);
aveFilt = dsp.MovingAverage('WindowLength', windowSize);
minFilt = dsp.MovingMinimum('WindowLength', windowSize);
maxFilt = dsp.MovingMaximum('WindowLength', windowSize);


medfilted = medFilt(signal);
avefilted = aveFilt(signal);
minfilted = minFilt(signal);
maxfilted = maxFilt(signal);

xx = 1:sigSize-floor(windowSize/2);
idx = floor(windowSize / 2)+1:sigSize;
plot(x, signal, xx, medfilted(idx), xx, avefilted(idx), xx, minfilted(idx), xx, maxfilted(idx)); 


formatSignal = @(signal) ['[' strjoin(arrayfun(@(x) num2str(x), signal, 'UniformOutput', false), ', ') ']'];
disp("Signal:")
disp(formatSignal(signal));  
