#!/bin/bash

# Script to build and serve multilingual documentation
# Usage: ./build-multilingual.sh

echo "Building multilingual MkDocs site..."

# Build both languages with single command using mkdocs-static-i18n
echo "Building English and French versions with mkdocs-static-i18n..."
mkdocs build --clean --site-dir site/

echo "✅ Multilingual site built successfully!"
echo ""
echo "Structure (generated by mkdocs-static-i18n):"
echo "  site/"
echo "  ├── index.html (English - default language)"
echo "  ├── with-license/"
echo "  ├── no-license/"
echo "  └── fr/"
echo "      ├── index.html (French)"
echo "      ├── with-license/"
echo "      └── no-license/"
echo ""
echo "To serve locally:"
echo "  cd site && python -m http.server 8000"
