from osm_ai_helper.utils.inference import split_area_into_lat_lon_centers


def test_split_area_into_lat_lon_centers():
    lat_lon_centers = split_area_into_lat_lon_centers("Ponteareas", zoom=14, margin=2)
    assert lat_lon_centers == [
        (42.23665188032057, -8.536376953124993),
        (42.15525946577863, -8.536376953124993),
        (42.23665188032057, -8.426513671875012),
        (42.15525946577863, -8.426513671875012),
    ]
    lat_lon_centers = split_area_into_lat_lon_centers("Ponteareas", zoom=14, margin=1)
    assert lat_lon_centers == [
        (42.25291778330197, -8.558349609375009),
        (42.204107493733176, -8.558349609375009),
        (42.15525946577863, -8.558349609375009),
        (42.10637370579324, -8.558349609375009),
        (42.25291778330197, -8.492431640624993),
        (42.204107493733176, -8.492431640624993),
        (42.15525946577863, -8.492431640624993),
        (42.10637370579324, -8.492431640624993),
        (42.25291778330197, -8.426513671875012),
        (42.204107493733176, -8.426513671875012),
        (42.15525946577863, -8.426513671875012),
        (42.10637370579324, -8.426513671875012),
    ]
